// streamConverterAPI/internal/middleware/concurrency_middleware.go
package middleware

import (
    "api/internal/model/response/common"
    "api/internal/repository/logger"
    "context"
    "github.com/cockroachdb/errors"
    "log"
    "net/http"
    "time"

    "github.com/gin-gonic/gin"
)
// RequestQueueItem 代表被排進佇列等待處理的請求，
// 使用 notify channel 來通知排到該請求可以開始執行。
type RequestQueueItem struct {
    notify chan struct{}
}

// ConcurrencyLimiterWithQueue 控制同時處理的請求數，
// 超過上限的請求會被放進 queue 中等待。
// 適合用來保護後端資源，例如資料庫、外部 API 等。
type ConcurrencyLimiterWithQueue struct {
    semaphore chan struct{}         // 控制並發數量，作用類似限流 token
    queue     chan RequestQueueItem // 請求等待佇列
    ctx       context.Context
    cancel    context.CancelFunc
    loggers   logger.ILoggers
}

// NewConcurrencyLimiterWithQueue 初始化限流器，並啟動處理 queue 的 goroutine。
func NewConcurrencyLimiterWithQueue(loggers logger.ILoggers, maxConcurrency, maxQueue int) *ConcurrencyLimiterWithQueue {
    ctx, cancel := context.WithCancel(context.Background())
    cl := &ConcurrencyLimiterWithQueue{
        semaphore: make(chan struct{}, maxConcurrency),
        queue:     make(chan RequestQueueItem, maxQueue),
        ctx:       ctx,
        cancel:    cancel,
        loggers:   loggers,
    }

    // 背景 goroutine 處理 queue 中排隊的請求
    go cl.processQueue()

    return cl
}

// Shutdown 清理資源與關閉 queue（應在服務關閉時呼叫）
func (cl *ConcurrencyLimiterWithQueue) Shutdown() {
    cl.cancel()
    close(cl.queue)
}

// processQueue 會不斷從 queue 中取出請求，
// 等待有空閒資源時發送通知讓請求繼續往下執行。
func (cl *ConcurrencyLimiterWithQueue) processQueue() {
    for {
        select {
        case <-cl.ctx.Done():
            return
        case item, ok := <-cl.queue:
            if !ok {
                return
            }
            // 取得一個可用位元（token）
            cl.semaphore <- struct{}{}
            // 通知該請求可以執行
            close(item.notify)
            cl.loggers.Info("Request dequeued and allowed", nil)
        }
    }
}

// Handle 是真正綁到 Gin 的 middleware function，
// 當併發達到上限時，請求會被送進 queue 等待，
// 若 queue 也滿了，就直接回應 429 Too Many Requests。
func (cl *ConcurrencyLimiterWithQueue) Handle() gin.HandlerFunc {
    return func(c *gin.Context) {
        select {
        // 有空位直接執行
        case cl.semaphore <- struct{}{}:
            cl.loggers.Info("Request allowed", c.Request.Body)
            defer func() {
                <-cl.semaphore
                cl.loggers.Info("Request completed and semaphore released", c.Request.Body)
            }()
            c.Next()
            return

        // 沒有空位，放進 queue 嘗試排隊
        default:
            notify := make(chan struct{})
            select {
            case cl.queue <- RequestQueueItem{notify: notify}:
                cl.loggers.Info("Request enqueued", c.Request.Body)

                select {
                // 被通知排到，繼續處理請求
                case <-notify:
                    cl.loggers.Info("Request dequeued", c.Request.Body)
                    defer func() {
                        <-cl.semaphore
                        cl.loggers.Info("Request completed and semaphore released", c.Request.Body)
                    }()
                    c.Next()

                // 排隊超過時間，自動超時處理
                case <-time.After(60 * time.Minute):
                    log.Println("Request timed out while waiting in queue")
                    c.JSON(http.StatusGatewayTimeout, gin.H{
                        "data": "",
                        "status": gin.H{
                            "code":    "70002",
                            "message": "Request timed out",
                        },
                    })
                    c.Abort()
                    return
                }

            // queue 也塞滿了，回應 429
            default:
                cl.loggers.Warn("Request blocked: queue full", c.Request.Body)
                c.JSON(http.StatusTooManyRequests, common.NewResponseErr(
                    errors.New("Too many requests"),
                    "Request blocked: queue full",
                    common.ErrTooManyConcurrency,
                ))
                c.Abort()
                return
            }
        }
    }
}

CREATE TABLE KeyValueStore (
    [Key]   NVARCHAR(256) NOT NULL PRIMARY KEY,
    [Value] NVARCHAR(MAX) NULL,
    [Version] INT NOT NULL DEFAULT 0
);

using Microsoft.Data.SqlClient;
using Dapper;

var builder = WebApplication.CreateBuilder(args);
var app = builder.Build();

// Database connection string (modify as appropriate for your environment)
string connString = builder.Configuration.GetConnectionString("KeyValueDb");

// 1. GET endpoint: retrieve value by key
app.MapGet("/kv/{key}", async (string key) =>
{
    const string sql = "SELECT [Value], [Version] FROM KeyValueStore WHERE [Key] = @Key";
    await using var conn = new SqlConnection(connString);
    var result = await conn.QueryFirstOrDefaultAsync<(string Value, int Version)>(sql, new { Key = key });
    if (result.Value == null)
    {
        return Results.NotFound($"Key '{key}' not found");
    }
    // Return the value and version (as ETag header for concurrency control, e.g.)
    return Results.Ok(new { Key = key, Value = result.Value, Version = result.Version });
});

// 2. PUT endpoint: insert or update a key-value
app.MapPut("/kv/{key}", async (string key, string value) =>
{
    await using var conn = new SqlConnection(connString);
    // Try update first
    const string updateSql = @"
        UPDATE KeyValueStore
        SET [Value] = @Value, [Version] = [Version] + 1
        WHERE [Key] = @Key;
        SELECT @@ROWCOUNT;";
    var rows = await conn.ExecuteScalarAsync<int>(updateSql, new { Key = key, Value = value });
    if (rows == 0)
    {
        // If key not exists, insert new record
        const string insertSql = "INSERT INTO KeyValueStore([Key],[Value],[Version]) VALUES(@Key, @Value, 0);";
        try
        {
            await conn.ExecuteAsync(insertSql, new { Key = key, Value = value });
        }
        catch (SqlException ex) when (ex.Number == 2627) // PK violation
        {
            // Handle race condition: key was inserted by another request in the meantime
            return Results.Conflict($"Key '{key}' was inserted by another client.");
        }
    }
    return Results.NoContent();
});

app.Run();

public sealed class ConsistentHashRing<T>
{
    private readonly SortedDictionary<int, T> _ring = new();
    private readonly int _replicas;
    private readonly MD5 _md5 = MD5.Create();

    public ConsistentHashRing(IEnumerable<T> nodes, int replicas = 160)
    {
        _replicas = replicas;
        foreach (var node in nodes) AddNode(node);
    }

    public void AddNode(T node)
    {
        for (int i = 0; i < _replicas; i++)
        {
            var hash = Hash($"{node}-{i}");
            _ring[hash] = node;
        }
    }

    public void RemoveNode(T node)
    {
        for (int i = 0; i < _replicas; i++)
        {
            var hash = Hash($"{node}-{i}");
            _ring.Remove(hash);
        }
    }

    public T GetNode(string key)
    {
        if (!_ring.Any()) throw new InvalidOperationException("Ring is empty");
        var hash = Hash(key);
        var kv = _ring.FirstOrDefault(p => p.Key >= hash);
        return kv.Equals(default(KeyValuePair<int, T>)) ? _ring.First().Value : kv.Value;
    }

    private int Hash(string input)
    {
        var bytes = _md5.ComputeHash(Encoding.UTF8.GetBytes(input));
        return BitConverter.ToInt32(bytes, 0) & 0x7FFFFFFF; // positive int
    }
}

