using System;

class GotoComplexExample {
    static void Main() {
        int num = 1;

        start:
        if (num == 5) {
            goto skipFive;
        }

        Console.WriteLine(num);
        num++;

        if (num <= 10) {
            goto start;
        }
        goto end;

        skipFive:
        num++;
        goto start;

        end:
        Console.WriteLine("Done");
    }
}

