public static int Factorial(int n)
{
    if (n <= 1) // Âü∫Êú¨ÊÉÖÊ≥Å
        return 1;
    else
        return n * Factorial(n - 1); // ÈÅûÊ≠∏Ê≠•È©ü
}

int result = Factorial(5); // Ë®àÁÆó 5!

