using System;

class GotoComplexExample {
    static void Main() {
        int num = 1;

        start:
        if (num == 5) {
            goto skipFive;
        }

        Console.WriteLine(num);
        num++;

        if (num <= 10) {
            goto start;
        }
        goto end;

        skipFive:
        num++;
        goto start;

        end:
        Console.WriteLine("Done");
    }
}

using System;

class StructuredComplexExample {
    static void Main() {
        for (int num = 1; num <= 10; num++) {
            if (num == 5) {
                continue; // 跳過5，直接繼續下一輪循環
            }
            Console.WriteLine(num);
        }
        Console.WriteLine("Done");
    }
}

