package main

import (
    "log"
    "math/rand"
    "sync"
    "time"
)

type DB struct {
    Value int
}
type Client struct {
    Name string
    wg   *sync.WaitGroup
}

func (c *Client) Read(db *DB) {
    defer c.wg.Done()
    for i := 0; i < 3; i++ {
        time.Sleep(time.Millisecond * time.Duration(rand.Intn(10)))
        log.Printf("%s x=%d %v \n", c.Name, db.Value, time.Now().UnixMilli())
    }
}

func (c *Client) Write(db *DB, i int) {
    defer c.wg.Done()
    time.Sleep(time.Millisecond * time.Duration(rand.Intn(10)))
    db.Value = i
}

func main() {
    rand.Seed(time.Now().UnixNano())
    wg := &sync.WaitGroup{}

    wg.Add(3)
    db := &DB{Value: 0}
    clientA := &Client{Name: "ClientA", wg: wg}
    clientB := &Client{Name: "ClientB", wg: wg}
    clientC := &Client{Name: "ClientC", wg: wg}

    go clientA.Read(db)
    go clientB.Read(db)
    go clientC.Write(db, 1)

    wg.Wait()
}
